'use strict';

var runSequence = require('run-sequence');

module.exports.loadDefaultTasks = function(gulp) {
    gulp.task('clean', require('./lib/task/clean').task);
    gulp.task('bower-install', require('./lib/task/bower-install').task);
    gulp.task('npm-install', require('./lib/task/npm-install').task);
    gulp.task('default', ['build']);
    gulp.task('build', function(cb) {
        runSequence.use(gulp)(
            'clean',
            ['npm-install', 'bower-install'],
            'webpack',
            cb
        );
    });
    gulp.task('webpack', require('./lib/task/webpack').build);
    gulp.task('watch', require('./lib/task/webpack').watch);
};
