'use strict';

var _ = require('lodash'),
    config = require('../config'),
    del = require('del'),
    path = require('path');

module.exports.task = function(cb) {
    var buildDirs = _.map(config.apps, function(app) {
        return path.resolve(app.basePath, 'build');
    });

    del(buildDirs, function(err) {
        if (err) return cb(err);
        cb();
    });
};
